mapper.select:
select count(1) as totalcnt from VTDATABASE_GROUP where 1=1
and REG_ID =#{userId}
and (GROUP_NAME like '%'||#{keyword}||'%' or GROUP_DESC like '%'||#{keyword}||'%')

select GROUP_ID, GROUP_NAME, GROUP_DESC, REG_ID, REG_DT ,to_char(a.REG_DT,'YYYY-MM-DD HH24:MI:SS') as CHAR_CRE_DT from VTDATABASE_GROUP a where 1=1
and REG_ID =#{userId}
and (GROUP_NAME like '%'||#{keyword}||'%' or GROUP_DESC like '%'||#{keyword}||'%') order by REG_DT desc OFFSET ${first}-1 ROWS FETCH FIRST ${countPerPage} ROWS ONLY

mapper.insert:
insert  into VTDATABASE_GROUP (GROUP_ID,GROUP_NAME,GROUP_DESC,REG_ID,REG_DT,UPD_ID,UPD_DT )
values(
	#{groupId}
	, #{groupName}
	, #{groupDesc}
	, #{userId}
	, current_date
	, #{userId}
	, current_date
);

mapper.update:
update VTDATABASE_GROUP
set
	GROUP_NAME = #{groupName}
	, GROUP_DESC = #{groupDesc}
	, UPD_ID = #{userId}
	, UPD_DT = current_date
where	GROUP_ID = #{groupId}

	and REG_ID =#{userId}

mapper.delete:
delete from VTDATABASE_GROUP 
where GROUP_ID = #{groupId}


	and REG_ID =#{userId}
