mapper.select:
select suba.*, nvl2(subb.VCONNID,'Y','N') as BLOCK_YN from (
	select a.VCONNID , a.VNAME, 


				 1 


				(select count(1) from VTDATABASE_MANAGER subt 
				 where subt.VCONNID = a.VCONNID 
				 and subt.VIEWID = #{dp_viewId} 
				) 


		as  MANAGER_CNT
	from VTCONNECTION a
	where 1=1 
	and (	
		a.VCONNID in (
			select distinct VCONNID from VTDATABASE_GROUP_DB where GROUP_ID in (
				select GROUP_ID  from VTDATABASE_GROUP_USER where VIEWID = #{viewid}
			)
		)
		or a.VCONNID in (select VCONNID from VTDATABASE_MANAGER where VIEWID =#{viewid} )
	)
	and a.USE_YN = 'Y'
) suba left outer join (select * from VTDATABASE_BLOCK_USER where VIEWID =#{viewid}) subb
on suba.VCONNID = subb.VCONNID

select count(1)  cnt
from VTDATABASE_MANAGER 
where VCONNID = #{vconnid} 
and VIEWID = #{dp_viewId}

mapper.delete:
delete from VTDATABASE_BLOCK_USER
where VCONNID = #{vconnid}
and VIEWID  = #{viewid}

mapper.update:
update VTUSER 
set 
	USER_ROLE =#{role}
	,ACCEPT_YN=#{acceptyn}
where VIEWID =#{viewid}

update VTQNA
set
	ANSWER =#{answer}
	,ANSWER_ID =#{userid}
	,ANSWER_DT = CURRENT_TIMESTAMP
where QNAID=#{qnaid}
