mapper.insert:
insert into VTSQL_LOG (
	VCONNID
	, VIEWID
	, START_TIME
	, S_MM
	, S_DD
	, S_HH
	, END_TIME
	, DELAY_TIME
	, LOG_SQL
	, USR_IP
	, RESULT_COUNT
	, COMMAND_TYPE
)
values( 
	#{vconnid}
	, #{viewid}
	, #{startTime}
	, #{sMm}
	, #{sDd}
	, #{sHh}
	, #{endTime}
	, #{delayTime}
	, #{logSql}
	, #{usrIp}
	, #{resultCount}
	, #{commandType}
)

insert  into VTSQL_SQLFILE (
	VIEWID
	, VCONNID
	, SQL_ID
	, GUERY_TITLE
	, QUERY_CONT
	, SQL_PARAM
	, REG_DT
	, UPD_DT
) 
values(
	#{userid}
	, #{vconnid}
	, #{sqlId}
	, #{sqlTitle}
	, #{sql}
	, #{sqlParam}
	, CURRENT TIMESTAMP
	, CURRENT TIMESTAMP
)

mapper.update:
update VTSQL_SQLFILE 
set
	UPD_DT = CURRENT TIMESTAMP


		, GUERY_TITLE = #{sqlTitle}


		, QUERY_CONT = #{sql}
		, SQL_PARAM =#{sqlParam}


where sql_id = #{sqlId}
and VCONNID = #{vconnid}
and VIEWID = #{userid}

update VTSQL_SQLFILE_TAB
set
	 VIEW_YN = 'N'
where VCONNID = #{vconnid} 
and VIEWID = #{userid}

update VTSQL_SQLFILE_TAB 
set
	VIEW_YN = 'Y'
where VCONNID = #{vconnid} 
and VIEWID = #{userid}
and sql_id = #{sqlId}

mapper.insert:
insert  into VTSQL_SQLFILE_TAB (VCONNID,VIEWID,PREV_SQL_ID,SQL_ID,VIEW_YN,REG_DT )
values(
	#{vconnid}
	, #{userid}
	, #{custom.prevTabId}
	, #{sqlId}
	, 'Y' 
	, CURRENT TIMESTAMP
)

mapper.update:
update VTSQL_SQLFILE_TAB
	set PREV_SQL_ID = (select max(PREV_SQL_ID) from VTSQL_SQLFILE_TAB where VCONNID = #{vconnid} and SQL_ID = #{sqlId})
where VCONNID = #{vconnid}
and VIEWID = #{userid}
and PREV_SQL_ID = #{sqlId}

mapper.delete:
delete from VTSQL_SQLFILE_TAB
where VCONNID = #{vconnid} 
and VIEWID = #{userid}
and SQL_ID = #{sqlId}

mapper.select:
select vsf.SQL_ID, vsf.VIEW_YN, vsf.PREV_SQL_ID, vsf.VCONNID , a.QUERY_CONT, a.GUERY_TITLE
from   VTSQL_SQLFILE_TAB vsf , VTSQL_SQLFILE a
where vsf.SQL_ID = a.SQL_ID
and vsf.VCONNID = #{vconnid}
and vsf.VIEWID = #{userid}
order by vsf.PREV_SQL_ID asc

select * from VTSQL_SQLFILE
where VCONNID = #{vconnid}
and VIEWID = #{userid}
and GUERY_TITLE like '%'||#{custom.searchVal}||'%' 
order by GUERY_TITLE asc

mapper.delete:
delete from VTSQL_SQLFILE
where VCONNID = #{vconnid}
and SQL_ID = #{sqlId}

mapper.insert:
insert  into VTSQL_HISTORY (
	VCONNID
	, VIEWID
	, HISTORYID
	, START_TIME
	, END_TIME
	, DELAY_TIME
	, LOG_SQL
	, USR_IP
	, ERROR_LOG 
) 
values(
	#{vconnid}
	, #{viewid}
	, #{historyId}
	, #{startTime}
	, #{endTime}
	, #{delayTime}
	, #{logSql}
	, #{usrIp}
	, #{errorLog} 
)

mapper.select:
select count(1) TOTAL_CNT
from VTSQL_HISTORY a
where 1=1
and VCONNID=#{customParam.conuid}
and VIEWID=#{customParam.uid}
and LOG_SQL like '%'||#{keyword}||'%'
