mapper.select:
select count(1)as CNT 
from VTUSER
where uid =#{uid}

mapper.insert:
insert  into VTUSER (
	VIEWID 
	, UID 
	, UPW 
	, UNAME 
	, ORG_NM 
	, DEPT_NM 
	, UEMAIL 
	, USER_ROLE 
	, DESCRIPTION 
	, ACCEPT_YN
	, REG_ID 
	, REG_DT
	, UPD_ID
	, UPD_DT
) 
values(
	#{viewid}
	, #{uid}
	, #{upw}
	, #{uname}
	, #{orgNm}
	, #{deptNm}
	, #{uemail}
	, 'GUEST'
	, #{description}
	, #{acceptYn}
	, #{creId}
	, CURRENT_TIMESTAMP
	, #{creId}
	, CURRENT_TIMESTAMP
)

mapper.select:
select c.* , d.UNAME as SEND_NM , d.UID  as SEND_ID
from (
	select a.MEMO_TITLE , a.MEMO_ID, a.MEMO_CONT, SEND_ID, to_char(REG_DT,'YYYY-MM-DD HH24:MI:SS') as REG_DT , VIEW_DT
	from VTMEMO a , VTMEMO_USER b 
	where a.MEMO_ID = b.MEMO_ID 
	and RECV_ID = #{uid}
	and VIEW_DT is null
	order by reg_dt desc
) c left outer join VTUSER d 
where c.SEND_ID  = d.VIEWID

mapper.insert:
insert  into VTMEMO (
	MEMO_ID
	, PARENT_MEMO_ID
	, MEMO_TITLE
	, MEMO_CONT
	, REG_ID
	, REG_DT
) 
values(
	#{memoId}
	, #{parentMemoId}
	, #{memoTitle}
	, #{memoCont}
	, #{regId}
	, CURRENT_TIMESTAMP
)

mapper.update:
update VTMEMO_USER 
set 
	VIEW_DT = CURRENT_TIMESTAMP
where RECV_ID = #{uid}
and MEMO_ID = #{memo_id}
