mapper.select:
select * 
from VTPREFERENCES 
where VCONNID =#{vconnid}
and VIEWID =#{userid}
and PREF_KEY =#{prefKey}

mapper.insert:
insert  into VTPREFERENCES (VCONNID,VIEWID,PREF_KEY,PREF_VAL,REG_DT,UPD_DT ) 
values(
	#{vconnid}
	, #{userid}
	, #{prefKey}
	, #{prefVal}
	, CURRENT_TIMESTAMP
	, CURRENT_TIMESTAMP
)

mapper.update:
update VTUSER 
set 
	 UNAME =#{uname}
	, ORG_NM =#{orgNm}
	, DEPT_NM =#{deptNm}
	, UEMAIL =#{uemail}
	, DESCRIPTION =#{description}
	, LANG =#{lang}
	, UPD_DT = CURRENT_TIMESTAMP
where VIEWID =#{viewid}

mapper.select:
select UPW
from VTUSER a
where VIEWID =#{viewid}

mapper.update:
update VTUSER 
set 
	 UPW = #{upw}
where VIEWID =#{viewid}

mapper.select:
select count(1) CNT from VTMEMO a
where REG_ID = #{customParam.uid}
, VTMEMO_USER b where a.MEMO_ID = b.MEMO_ID and RECV_ID = #{customParam.uid}
and MEMO_TITLE like '%'||#{keyword}||'%'

mapper.delete:
delete from VTMEMO_USER 
where 1=1


		and SEND_ID = (select REG_ID from VTMEMO WHERE MEMO_ID =#{memoId})
		and VIEW_DT is null


		and RECV_ID = #{uid}


and MEMO_ID =#{memoId}

mapper.insert:
insert  into VTQNA (
	QNAID 
	, TITLE 
	, QUESTION 
	, REG_ID 
	, REG_DT 
)
values(
	#{qnaid}
	, #{title}
	, #{question}
	, #{userid}
	, CURRENT_TIMESTAMP
)

mapper.update:
update VTQNA 
set
	DEL_YN ='Y'
where QNAID=#{qnaid}
and REG_ID = #{userid}
and ANSWER_ID IS NULL

update VTQNA
set
	TITLE =#{title}
	,QUESTION =#{question}
	,UPD_DT = CURRENT_TIMESTAMP
where QNAID=#{qnaid}
and REG_ID = #{userid}
and ANSWER_ID IS NULL

update VTPREFERENCES 
set
	PREF_VAL = #{prefVal}
	, UPD_DT = CURRENT_TIMESTAMP
where	VCONNID = #{vconnid} 
and	VIEWID = #{userid} 
and	PREF_KEY = #{prefKey}

mapper.select:
select 
	count(1) cnt
from VTSQL_SQLFILE
where VIEWID =#{customParam.uid}

	and  VCONNID = #{customParam.vconnid}

and (GUERY_TITLE like '%'||#{keyword}||'%' or QUERY_CONT like '%'||#{keyword}||'%')

select a.SQL_ID , a.GUERY_TITLE , to_char(a.REG_DT,'YYYY-MM-DD HH24:MI:SS') as CHAR_REG_DT , b.VNAME from VTSQL_SQLFILE a left outer join VTCONNECTION b on a.VCONNID = b.VCONNID where VIEWID =#{customParam.uid}
and VCONNID = #{customParam.vconnid}
and (GUERY_TITLE like '%'||#{keyword}||'%' or QUERY_CONT like '%'||#{keyword}||'%')
