mapper.update:
update vtuser 
set 
	role =#{role}
	,accept_yn=#{acceptyn}
where viewid =#{viewid}

update vtqna
set
	answer =#{answer}
	,upd_id =#{cre_id}
	,upd_dt = CURRENT TIMESTAMP
where qnaid=#{qnaid}

insert into vtdatabase_user_role (vconnid, viewid , upd_id ,upd_dt)
values(
	#{vconnid}
	, #{viewid}
	, #{upd_id}
	,CURRENT TIMESTAMP
)

mapper.select:
	select 
	COMMAND_TYPE X_COL, count(1) as Y_COL
from VTSQL_LOG a
where	vconnid=#{vconnid}
and start_time between #{s_date} and	#{e_date}
group by COMMAND_TYPE

	select b.uname label , cnt as data from 
	(
		select 
			UID, count(1) cnt 
		from VTSQL_LOG a
		where	vconnid=#{vconnid}
		and start_time between #{s_date} and	#{e_date}

AND command_type =#{command_type}

		group by UID 
		order by cnt desc	  
		FETCH FIRST 5 ROWS ONLY
	) a , vtuser b
	where a.uid = b.viewid
