mapper.insert:
insert into vtsql_log (
	vconnid
	, uid
	, start_time
	, s_mm
	, s_dd
	, s_hh
	, end_time
	, delay_time
	, log_sql
	, result_count
	, command_type
)
values( 
	#{vconnid}
	, #{uid}
	, #{start_time}
	, #{s_mm}
	, #{s_dd}
	, #{s_hh}
	, #{end_time}
	, #{delay_time}
	, #{log_sql}
	, #{result_count}
	, #{command_type}
)
