mapper.select:
select count(1)as cnt 
from vtuser
where uid =#{uid}

mapper.insert:
insert  into vtuser (
	viewid 
	, uid 
	, upw 
	, uname 
	, org_nm 
	, dept_nm 
	, uemail 
	, role 
	, description 
	, accept_yn
	, cre_id 
	, upd_id
	, cre_dt
) 
values(
	#{viewid}
	, #{uid}
	, #{upw}
	, #{uname}
	, #{org_nm}
	, #{dept_nm}
	, #{uemail}
	, 'GUEST'
	, #{description}
	, #{accept_yn}
	, #{cre_id}
	, #{cre_id}
	, CURRENT TIMESTAMP
)

mapper.update:
update vtuser 
set 
	upw=#{upw}
	, uname =#{uname}
	, org_nm =#{org_nm}
	, dept_nm =#{dept_nm}
	, uemail =#{uemail}
	, description =#{description}
	, upd_dt = CURRENT TIMESTAMP
where viewid =#{viewid}

mapper.insert:
insert  into vtqna (
	qnaid 
	, title 
	, question 
	, cre_id 
	, cre_dt 
	, del_yn
	, answer
)
values(
	#{qnaid}
	, #{title}
	, #{question}
	, #{cre_id}
	, CURRENT TIMESTAMP
	, 'N'
	, ''
)

mapper.update:
update vtqna
set
	title =#{title}
	,question =#{question}
where qnaid=#{qnaid}
