-- 데이터 베이스 그룹 & db mapping 정보. CREATE TABLE VTDATABASE_GROUP_DB ( GROUP_ID VARCHAR2(36) NOT NULL , VCONNID VARCHAR(5) NOT NULL , REG_ID VARCHAR(36) , REG_DT TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL ,CONSTRAINT PK_VTDATABASE_GROUP_DB PRIMARY KEY (GROUP_ID, VCONNID) );
