apply.sql:
-- TASK_ 기본 정보 CREATE TABLE VTTASK( TASK_ID VARCHAR(36) NOT NULL -- TASK_ id ,TASK_NAME VARCHAR(36) NOT NULL -- TASK_명 ,TASK_TYPE VARCHAR(10) NOT NULL -- TASK_ 타입 ,USE_YN CHAR(1) default 'Y' -- 사용여부 ,DESCRIPTION VARCHAR(1000) -- 설명 ,REG_ID VARCHAR(36) -- 등록자 ,REG_DT TIMESTAMP DEFAULT CURRENT_TIMESTAMP --등록일 ,UPD_ID VARCHAR(36) -- 수정자 ,UPD_DT TIMESTAMP DEFAULT CURRENT_TIMESTAMP -- 수정일 ,CONSTRAINT VTTASK PRIMARY KEY (TASK_ID) );
-- TASK_ SQL CREATE TABLE VTTASK_SQL( TASK_ID VARCHAR(36) NOT NULL -- TASK_ id ,VCONNID VARCHAR(5) NOT NULL ,SQL CLOB ,PARAMETER VARCHAR(4000) -- 파라미터 ); ALTER TABLE VTTASK_SQL ADD CONSTRAINT FK_VTTASK_SQL FOREIGN KEY (TASK_ID) REFERENCES VTTASK (TASK_ID);
CREATE TABLE VTTASK_TRANSFER( TASK_ID VARCHAR(36) NOT NULL -- TASK_ id ,COMMIT_COUNT int ,SOURCE_VCONNID VARCHAR(5) NOT NULL ,SOURCE_CONFIG CLOB -- SQL , 설정 정보가 들어감 JSON으로 ,TARGET_VCONNID VARCHAR(5) NOT NULL ,TARGET_CONFIG CLOB );
ALTER TABLE VTTASK_TRANSFER ADD CONSTRAINT FK_VTTASK_TRANSFER FOREIGN KEY (TASK_ID) REFERENCES VTTASK (TASK_ID);
CREATE TABLE VTTASK_HISTORY( HIST_SEQ BIGINT NOT NULL , INSTANCE_ID VARCHAR(120) NOT NULL , TASK_ID VARCHAR(32) NOT NULL , RUN_TYPE VARCHAR(5) DEFAULT 'BATCH' NOT NULL , START_TIME TIMESTAMP NOT NULL , END_TIME TIMESTAMP NOT NULL , RUN_TIME BIGINT , RESULT_COUNT BIGINT , FAIL_COUNT BIGINT , MESSAGE VARCHAR(2000) , STATUS VARCHAR(20) , CUSTOM_INFO VARCHAR(3000) );

revert.sql:
drop table VTTASK; 
drop table VTTASK_SQL; 
drop table VTTASK_TRANSFER;
