mapper.select:
select a.* ,b.* from ( SELECT ui.INDEX_NAME ,ui.TABLE_NAME ,ui.TABLESPACE_NAME as "TABLE_SPACE" ,ui.UNIQUENESS ,ui.TABLE_OWNER ,ui.BUFFER_POOL ,CASE WHEN uc.CONSTRAINT_TYPE = 'P' THEN 'PK' WHEN ui.UNIQUENESS = 'UNIQUE' THEN 'UQ' ELSE '' END AS "TYPE" ,uc.STATUS
FROM USER_INDEXES ui left outer join USER_CONSTRAINTS uc ON ui.TABLE_NAME = uc.TABLE_NAME AND ui.INDEX_NAME = uc.CONSTRAINT_NAME where 1 =1 AND uc.STATUS = 'ENABLED'
FROM ALL_INDEXES ui left outer join ALL_CONSTRAINTS uc ON ui.OWNER = uc.OWNER and ui.TABLE_NAME = uc.TABLE_NAME AND ui.INDEX_NAME = uc.CONSTRAINT_NAME where ui.OWNER = upper(#{schema}) AND uc.STATUS = 'ENABLED'
ui.INDEX_NAME in (${item})
) a , ( select INDEX_NAME ,COLUMN_NAME ,COLUMN_POSITION as "ORDINAL_POSITION" ,DESCEND as "ASC_OR_DESC"
from USER_IND_COLUMNS
from ALL_IND_COLUMNS where INDEX_OWNER = upper(#{schema})
)b where a.INDEX_NAME = b.INDEX_NAME ORDER BY a.TABLE_NAME, a.INDEX_NAME , b.ORDINAL_POSITION
