table.create:

{{~#if ddlOpt.addDropClause}}
/*DROP TABLE {{objectName}}; */
{{/if}}

CREATE TABLE {{objectName}}(
{{#columnList}}
    {{addChar @index "" ","}} {{COLUMN_NAME}} {{ddlTableValue 'typeAndLength' this dbType}} {{{ddlTableValue 'default' this dbType}}} {{{ddlTableValue 'nullable' this dbType}}}
{{/columnList}}
)

{{#ddlTableKey keyList objectName dbType}}
    {{#xif type '==' 'PK'}}
    {{#each constList}}
ALTER TABLE {{../objectName}} ADD CONSTRAINT {{@key}} PRIMARY KEY ({{~#this}}{{addChar @index "" ","}}{{COLUMN_NAME}}{{/this}});
    {{/each}}
    {{/xif}}
    {{#xif type '==' 'UQ'}}
    {{#each constList}}
CREATE UNIQUE INDEX {{@key}} ON {{../objectName}} ({{~#this}}{{addChar @index "" ","}}{{COLUMN_NAME}}{{/this}});
    {{/each}}
    {{/xif}}
{{/ddlTableKey}}

{{#commentsList}}
{{#xif TYPE '==' 'TBL'}}ALTER TABLE {{../objectName}} COMMENT = '{{COMMENT}}';{{/xif}}
{{~#xif TYPE '==' 'COL'}}ALTER TABLE {{../objectName}} MODIFY COLUMN {{NAME}} {{DATA_TYPE}} {{#equals true ddlOption.isNotNull}} not null {{/equals}} COMMENT '{{COMMENT}}';{{/xif}}
{{/commentsList}}

table.merge:
MERGE INTO {{TABLE_NAME}} AS a
USING (SELECT 1 AS dual) AS b
   ON (a.deptno = 50)
 WHEN MATCHED THEN
   UPDATE SET 
   		{{#columnList}}
		    {{addChar @index "" ","}} a.{{COLUMN_NAME}} =''
		{{/columnList}}
 WHEN NOT MATCHED THEN
   INSERT(
	   	{{#columnList}}
		    {{addChar @index "" ","}} {{COLUMN_NAME}}
		{{/columnList}}
	) VALUES(
		{{#columnList}}
		    {{addChar @index "" ","}} ''
		{{/columnList}}
	)

index.create:
{{#if ddlOpt.addDropClause}}
/*DROP INDEX {{objectName}}; */
{{/if}}

{{#with items.[0]}}
create {{ddlIndexKeyword 'unique' this}} index {{INDEX_NAME}}
on {{TABLE_NAME}}
{{/with~}}
({{~#items}}{{addChar @index "" ", "}}{{COLUMN_NAME}}{{ddlIndexKeyword 'ascDesc' this}}{{/items}})

sequence.create:
{{#equals true ddlOption.addDropClause}}/* DROP SEQUENCE {{OBJECT_NAME}}; */
{{/equals}}

CREATE SEQUENCE {{OBJECT_NAME}}
START WITH 1
INCREMENT BY {{INCREMENT_BY}}
MINVALUE {{MIN_VALUE}}
MAXVALUE {{MAX_VALUE}}
