{{#columnList}}
    {{addChar @index "" ","}} {{name}} {{ddlTableValue 'typeAndLength' this dbType}} {{{ddlTableValue 'default' this dbType}}} {{{ddlTableValue 'nullable' this dbType}}}
{{/columnList}}
)

{{#xif sourceText '!=' ''}}{{{sourceText}}}{{/xif}}

