table.create:

{{~#if ddlOpt.addDropClause}}
/*DROP TABLE {{objectName}}; */
{{/if}}

CREATE TABLE {{objectName}}(
{{#columnList}}
	{{addChar @index "" ","}} {{COLUMN_NAME}} {{ddlTableValue 'typeAndLength' this dbType}} {{{ddlTableValue 'default' this dbType}}} {{{ddlTableValue 'nullable' this dbType}}}
{{/columnList}}
)

{{#ddlTableKey keyList objectName dbType}}
  {{#xif type '==' 'PK'}}
	{{#each constList}}
ALTER TABLE {{../objectName}} ADD CONSTRAINT {{@key}} PRIMARY KEY ({{~#this}}{{addChar @index "" ","}}{{COLUMN_NAME}}{{/this}});
	{{/each}}
  {{/xif}}
  {{#xif type '==' 'UQ'}}
	{{#each constList}}
CREATE UNIQUE INDEX {{@key}} ON {{../objectName}} ({{~#this}}{{addChar @index "" ","}}{{COLUMN_NAME}}{{/this}});
	{{/each}}
  {{/xif}}
{{/ddlTableKey}}

{{#commentsList}}
{{#xif TYPE '==' 'TBL'}}EXEC sp_addextendedproperty 'MS_Description', '{{COMMENT}}', 'SCHEMA', {{../schema}}, 'TABLE', {{../objectName}};{{/xif}}
{{~#xif TYPE '==' 'COL'}}EXEC sp_addextendedproperty 'MS_Description', '{{COMMENT}}', 'SCHEMA', {{../schema}}, 'TABLE', {{../objectName}}, 'COLUMN', {{NAME}};{{/xif}}
{{/commentsList}}

table.constraintKey:
{{#ddlTableKey keyList objectName dbType}}
  {{~#xif type '==' 'PK'}}
	{{~#each constList}}
ALTER TABLE {{../objectName}} ADD CONSTRAINT {{@key}} PRIMARY KEY ({{~#this}}{{addChar @index "" ", "}}{{COLUMN_NAME}}{{/this}});
	{{/each~}}
  {{/xif~}}

  {{~#xif type '==' 'FK'}}
	{{~#each constList}}
ALTER TABLE {{../objectName}} ADD CONSTRAINT {{@key}} FOREIGN KEY ({{~#this}}{{addChar @index "" ", "}}{{COLUMN_NAME}}{{/this}}) REFERENCES {{#this}}{{#if @first}}{{REFERENCED_TABLE_NAME}}{{/if}}{{/this}}({{~#this}}{{addChar @index "" ", "}}{{REFERENCED_COLUMN_NAME}}{{/this}});
	{{/each~}}
  {{/xif~}}

  {{~#xif type '==' 'UQ'}}
	{{~#each constList}}
CREATE UNIQUE INDEX {{@key}} ON {{../objectName}} ({{~#this}}{{addChar @index "" ", "}}{{COLUMN_NAME}}{{/this}});
	{{/each~}}
  {{/xif~}}

{{/ddlTableKey}}

function.create:
CREATE FUNCTION {{OBJECT_NAME}} ({{ARGUMENTS}}) RETURN {{RETURN_TYPE}}
AS LANGUAGE {{LANG}}
NAME {{SOURCE}};

procedure.create:
CREATE FUNCTION {{OBJECT_NAME}} ({{ARGUMENTS}}) RETURN {{RETURN_TYPE}}
AS LANGUAGE {{LANG}}
NAME {{SOURCE}};

sequence.create:
{{#equals true ddlOption.addDropClause}}/* DROP SEQUENCE {{OBJECT_NAME}}; */
{{/equals}}

CREATE SEQUENCE {{OBJECT_NAME}}
START WITH 1
INCREMENT BY {{INCREMENT_BY}}
MINVALUE {{MIN_VALUE}}
MAXVALUE {{MAX_VALUE}}
